INSERT INTO `ristr8to_baristas` (`baristas_id`, `baristas_name`, `baristas_desc`, `baristas_facebook`, `baristas_instagram`, `baristas_twitter`, `baristas_order`) VALUES
(1, 'Arnon Thitiprasert (Tong)', '– Founder of Ristr8to, Ristr8to Lab, and Doppio Ristr8to in Chiang Mai, Thailand\r\n– 6th place World Latte Art champion 2011\r\n– 5th place World Latte Art champion 2015\r\n- 1st prize National Thailand Latte Art Championship 2015\r\n- 1st prize National Thailand Latte Art Championship 2016\r\n– Spending 4 years in Australia learning and working in coffee industry, then traveling to more than 30 countries to explore more about coffee culture around the world, now having fun running my owned 3 coffee shops in Chiang Mai sharing those experience with my staffs and my customers.\r\n\r\n', 'Doppio.Ristretto', 'ristr8to', 'ristr8to', 1),
(2, 'Kamonporn Fongsak (Paeng) ', ' เป็นศิลปะที่สนุก เราชอบคิด ชอบออกแบบ ท้าทายที่ได้ทำ พอวาดเสร็จ เราก็ได้แสดงผลงานด้วย แค่พื้นที่แสดงผลงานของเราไม่ใช่ gallery แต่มันคือ cafe ที่มีคนมากมาย แวะเวียนมาดูผลงานของเราทุกวัน และ canvas ของเรา ก็คือฟองนมสีขาว ในแก้วกาแฟ นี่แหละ  -Barista Paeng-', 'auzsawin', 'auzsawin_ristr8to', '', 2),
(3, 'Eakkapot  Wongsang  (Mak)', '', 'eakkapot.wongsang', 'mak_ristr8to', '', 3),
(4, 'Phuwanont Chansiri (Liew)', 'งานต้องเป็นที่หนึ่ง เเต่เมียเหนือกว่าทุกอย่าง', 'Phuwanont?ref=ts&fref=ts', 'lieweieiii', '', 4),
(5, 'Jirawat Jandee (Nook)', '', 'profile.php?id=100002573722622&fref=ts', '', '', 5);

