INSERT INTO `ristr8to_coffee` (`coffee_id`, `coffee_type`, `coffee_name`, `coffee_continent`, `coffee_roast`, `coffee_detail`, `coffee_mix`, `coffee_flavor`, `coffee_price_s`, `coffee_price_l`, `coffee_brew`, `coffee_order`, `coffee_show`, `coffee_promo`, `bento_url`) VALUES
(1, 2, 'Thailand', 4, 48, 'Various farms in CNX; A; Washed; 1350m - 1550m; Catimor; Well balanced of caramel and roasted peanut with little spice finish, medium body', '', '4;2;3;3;3', 328, 580, '|2||3||4|', 1, 1, 0, 'https://store.ristr8to.com/th/product/443716/product-443716?category_id=95185'),
(2, 2, 'Colombia', 2, 69, 'Norte de Santander; Supremo (17-18 scrn); Wet process; 1600m - 1800m; Typica; Smoky, bitter-sweet dark cacao with hint of caramel, full body, low berry-tone acidity', '', '4;2;3;3;4', 468, 880, '|2||3||4|', 2, 1, 0, 'https://store.ristr8to.com/th/product/443714/product-443714?category_id=95185'),
(3, 2, 'Costa Rica', 1, 45, 'CERRO AZUL; SHB (above 1200m); Wet process; 1600m - 1800m; Caturra; sweet peach, honey, subtle tones of toasted almond, medium body with refreshing acid', '', '3;4;3;3;3', 468, 880, '|1||5||6||7|', 3, 1, 0, 'https://store.ristr8to.com/th'),
(4, 2, 'Ethiopia', 3, 33, 'Yirgacheffe; 1 (4-12 defects); Wet process; 1700m - 1900m; Heirloom; Juicy and berry, subtle tones of tangerine & citrus, light body with refreshing acid', '', '2;5;2;5;2', 468, 880, '|1||5||6||7|', 4, 1, 0, 'https://store.ristr8to.com/th/product/443705/product-443705?category_id=95185'),
(5, 2, 'Brazil', 2, 58, 'Poços de Caldas; SS (Strictly Soft); Pulped-natural; 1100m - 1300m; Bourbon; Nutty, earthy, bitter sweet peanut butter with slightly Hazelnut aroma, full body', '', '4;2;3;2;4', 468, 880, '|2||3||4|', 5, 1, 0, 'https://store.ristr8to.com/th'),
(6, 2, 'Papua New Guinea', 4, 40, 'SIGRI; A; Fully washed; 1500m - 1700m; Blue mountain; Earthy, cinnamon mild chocolate with hint of spice nutmeg aroma and light body', '', '3;4;2;4;3', 468, 880, '|1||3||8|', 6, 1, 0, 'https://store.ristr8to.com/th'),
(7, 2, 'Guatemala', 1, 47, 'ANTIGUA; SHB (above 1350m); Wet process; 1500m - 1700m; Bourbon; Freshly plum-like, sweet dark berry, floral & hint of spice aroma, medium body', '', '3;4;1;4;3', 468, 880, '|1||2||5||8|', 7, 1, 0, 'https://store.ristr8to.com/th/product/443708/product-443708?category_id=95185'),
(8, 1, 'Black hand Blend', 0, 51, 'Our organic blend is a combination of organic certificated coffee from around the world to subserve coffee from organic farms in Thailand as in this blend we focus in give and take situation and take environment maintainance as important as great coffee flavor. Our goal is to support organic farms especially our local farms along with our relationship to improve quality of coffee from their growing and processing, then make the best blend based on their coffee.', 'Thailand|40|Various farms : Direct Trade;Colombia|30|Kachalu : Organic (Rainforest Alliance);Mexico|30|Chiapas : Organic (USDA), Fair Trade Certified', '5;2;3;4;3', 448, 880, '|2||3||4|', 8, 1, 0, 'https://store.ristr8to.com/th/product/443703'),
(9, 2, 'test', 1, 65, 'Origin; Grade; Process; Elevation; Varietal; Taste', '', '3;3;3;3;3', 100, 200, '', 9, 0, 0, ''),
(10, 1, 'RISTR8TO BLEND', 0, 50, 'This blend created with Asian, African and south American coffees has perfect balance between the acidity, sweetness, and body. Juicy and berry aroma of Ethiopian coffee balanced with nutty and bitter sweet chocolate of Brazilian coffee come along with earthy foundation of Papua new Guinean coffee resulting in a very complex tone but well round compliment flavor. Our medium roasting cut some overwhelming acidity while giving the blend full body to produce a smooth rich cup either by itself or with some milk. ', 'ETHIOPIA |40|Yirgacheffe : grade 2;PNG|30|Sigri : grade A;BRAZIL|30|Cerrado : grade SS', '3;3;3;3;3', 448, 880, '|1||2||3||5|', 10, 1, 0, 'https://store.ristr8to.com/th/product/443674/product-443674?category_id=95185'),
(11, 1, 'DECAF. BLEND', 0, 50, 'We use water from the mountain coasts of British Columbia to gently remove the caffeine until the coffee beans are 99% caffeine-free. No chemicals are used in the entire process while maintaining the bean\'s distinctive origin and flavor characteristics ( well-rounded light body, good acidity with slightly smokey cacao taste for our blend ).', 'COLOMBIA |70|Santa Barbara : grade Supremo;GUATEMALA |30|Antigua : grade SHB;||', '3;3;3;3;3', 448, 880, '|1||2||3||4||6||7|', 11, 1, 0, 'https://store.ristr8to.com/th/product/443701/product-443701?category_id=95185');

